import React from "react";
import { secondsToDuration } from "../utils/duration/index";

const FocusDuration = ({ clockTimes, setClockTimes }) => {
	const handleFocusChange = (event) => {
		const newTime = (event.target.id === "decrease-focus") ? 0 - (5 * 60) : (5 * 60);
		if(newTime > 60 * 60) {
			newTime = 60 * 60;
		}
		else if(newTime < 5 * 60) {
			ne
		}
		setClockTimes({
			...clockTimes,
			focusSetting: clockTimes.focusSetting + newTime,
		});
	}

	return (
		<div className="col">
			<div className="input-group input-group-lg mb-2">
				<span className="input-group-text" data-testid="duration-focus">
					{/* DONE: Update this text to display the current focus session duration */}
					Focus Duration: {secondsToDuration(clockTimes.focusSetting)}
				</span>

				<div className="input-group-append">
					{/* TODO: Implement decreasing focus duration and disable during a focus or break session */}
					<button
						type="button"
						className="btn btn-secondary"
						data-testid="decrease-focus"
						onClick={handleFocusChange}
						id="decrease-focus"
					>
						<span className="oi oi-minus" id="decrease-focus" />
					</button>
					{/* TODO: Implement increasing focus duration  and disable during a focus or break session */}
					<button
						type="button"
						className="btn btn-secondary"
						data-testid="increase-focus"
					>
						<span className="oi oi-plus" />
					</button>
				</div>
			</div>
		</div>
	);
}

export default FocusDuration;
