import React from "react";



const ProgressBar = ({ clockTimes }) => {

	const percent = () => {
		const mode = clockTimes.focus ? "focus" : "break";
		return clockTimes.focus ? 
						100 - (100 * (clockTimes.focusRemaining / clockTimes.focusSetting)) :
						100 - (100 * (clockTimes.breakRemaining / clockTimes.breakSetting));
		// 100 -> 0
		// 0 -> 100
		// 50 -> 50
		// 75 -> 25
		// 25 -> 75
		// 18 -> 82
	}

	return (
		<div className="row mb-2">
          <div className="col">
            <div className="progress" style={{ height: "20px" }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow={percent()} // TODO: Increase aria-valuenow as elapsed time increases
                style={{ width: `${percent()}%` }} // TODO: Increase width % as elapsed time increases
              />
            </div>
          </div>
        </div>
	);
}

export default ProgressBar;
